function plotSTDerrBars(H, x, y, stderrs, LW, barColor)% plotSTDerrBars(H, x, y, stderrs, LW, barColor)% plots the standard error bars associated with the data in x and y% H:	Handle to figure in which the bars will be plotted% x:	x-coordinate of the data vector% y:	y-coordinate of the data vector (means)% stderrs: standard error vector for the data% LW: bar line width% barColor: the color of the error bar% 'a': aquamarine% 'b': blue% 'c': cyan% 'd': dark red% 'g': green% 'k': black% 'm': magenta% 'o': copper% 'r': red% 'w': white% 'y': yellow%5/13/2001 Murat Okatan%5/29/2001 added the color option. Murat OkatanL=length(x);		%length of data vectorminX=min(x);maxX=max(x);capLength=(maxX-minX)/100;  %length of the horizontal line segments 							%that delimit the error bars at the top 							%and bottomswitch (barColor)    case 'a'        colorVect=[0.49 1 0.83];    case 'b'        colorVect=[0 0 1];    case 'c'        colorVect=[0 1 1];    case 'd'        colorVect=[0.5 0 0];    case 'g'        colorVect=[0 1 0];    case 'k'        colorVect=[0 0 0];    case 'm'        colorVect=[1 0 1];    case 'o'        colorVect=[1 0.62 0.40];    case 'r'        colorVect=[1 0 0];    case 'w'        colorVect=[1 1 1];    case 'y'        colorVect=[1 1 0];    otherwise        disp('Unknown color. Try: a-b-c-d-g-k-m-o-r-w-y');        return;endfigure(H); hold on;for i=1:L,	LH=line([1 1]*x(i), [-1 1]*stderrs(i)+y(i)); 			    %error bar	set(LH, 'LineWidth', LW, 'Color', colorVect);	LH=line([-1 1]*capLength+x(i), [1 1]*stderrs(i)+y(i));		%top delimiter	set(LH, 'LineWidth', LW, 'Color', colorVect);	LH=line([-1 1]*capLength+x(i), [-1 -1]*stderrs(i)+y(i));	%bottom delimiter	set(LH, 'LineWidth', LW, 'Color', colorVect);end